import React, { useContext, useEffect, useState } from 'react'
import CategoryContext from '../context/category/categoryContext'
import productContext from '../context/product/productContext'
import VariationContext from '../context/variation/variationContext'

const AddProductModal = () => {


  // for product context
  const pContext = useContext(productContext)
  const { addProduct } = pContext

  // for category context
  const cContext = useContext(CategoryContext)
  const { categories, getCategories } = cContext
  console.log("categories", categories);
  // variation context
  const vContext = useContext(VariationContext)
  const { variation, getVariations } = vContext
  console.log("variation", variation?.size);
  const [selectedProductColor, setSelectedProductColor] = useState(
    variation ? variation?.color : ""
  );
  console.log("selectedProductColor", selectedProductColor);
  const [selectedProductSize, setSelectedProductSize] = useState(
    variation ? variation?.size?.name : ""
  );
  console.log("selectedProductSize", selectedProductSize);
  useEffect(() => {
    getCategories();
    getVariations();
    // eslint-disable-next-line
  }, [])

  const [product, setProduct] = useState({
    name: '',
    sku: '',
    category: '',
    price: '',
    description: '',
    discount: '',
    saleCount: '',
    Stock: '',
    variation: ''
  })

  const [image, setImage] = useState(null)

  const handleChange = e => {
    setProduct({ ...product, [e.target.name]: e.target.value })
  }

  const handleAddproduct = () => {
    const { name, sku, category, price, description, discount, saleCount, Stock, variation } = product
    const formData = new FormData()
    formData.append('image', image)
    formData.append('name', name)
    formData.append('sku', sku)
    formData.append('category', category)
    formData.append('price', price)
    formData.append('description', description)
    formData.append('discount', discount)
    formData.append('saleCount', saleCount)
    formData.append('Stock', Stock)
    formData.append('variation', variation)
    console.log('Thêm sản phẩm để chạy')
    addProduct(formData)
    console.log('Thêm sản phẩm chạy')
    setProduct({
      name: '',
      sku: '',
      category: '',
      price: '',
      description: '',
    })
    setImage('')
  }

  return (
    <>
      {/* ADD POST MODAL */ }
      <div
        style={ { zIndex: '9999' } }
        className="modal fade"
        id="addProductModal">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header bg-primary text-white">
              <h5 className="modal-title">Thêm sản phẩm</h5>
              <button className="close" data-dismiss="modal">
                <span>×</span>
              </button>
            </div>
            {/* <form onSubmit={handleAddproduct}> */ }
            <div className="modal-body">
              <div className="form-group">
                <label htmlFor="name">Tên sản phẩm</label>
                <input
                  type="text"
                  name="name"
                  onChange={ handleChange }
                  value={ product.name }
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label htmlFor="sku">Mã sản phẩm</label>
                <input
                  type="text"
                  name="sku"
                  onChange={ handleChange }
                  value={ product.sku }
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label htmlFor="category">Danh mục</label>
                <select
                  className="form-control"
                  name="category"
                  onChange={ handleChange }>
                  <option value>Chọn danh mục</option>
                  { categories.map(item => (
                    <option key={ item._id } value={ item._id }>
                      { item.title }
                    </option>
                  )) }
                </select>
              </div>
              { variation ? (
                <>
                  <div className="form-group">
                    <label htmlFor="category">Variation</label>

                    <select
                      className="form-control"
                      name="category"
                      onChange={ handleChange }>
                      <option value>Select Color</option>
                      { variation.map(item => (
                        <option key={ item._id } value={ item.color }
                          onChange={ () => {
                            setSelectedProductColor(item.color);
                            setSelectedProductSize(item.size?.name);

                          } }
                        >
                          { item.color }
                        </option>

                      )) }
                    </select>
                    {/* { variation.map((single, key) => {
                      return (
                        <label
                          className={ `pro-details-color-content--single ${single.color}` }
                          key={ key }
                        >
                          <input
                            type="radio"
                            value={ single.color }
                            name="product-color"
                            checked={
                              single.color === selectedProductColor
                                ? "checked"
                                : ""
                            }
                            onChange={ () => {
                              setSelectedProductColor(single.color);
                              setSelectedProductSize(single.size?.name);
                            } }
                          />
                          <span className="checkmark">{ single.color }</span>
                        </label>
                      );
                    }) } */}
                  </div>
                  <div className="form-group">
                    <label htmlFor="category">Variation</label>
                    <select
                      className="form-control"
                      name="category"
                      onChange={ handleChange }>
                      <option value>Select Size</option>
                      { variation && variation.map(single => {
                        return single.color === selectedProductColor
                          ? single.size.map((singleSize, key) => {
                            return (
                              <>
                                <option key={ singleSize._id } value={ singleSize._id } onChange={ () => {
                                  setSelectedProductSize(
                                    singleSize.name
                                  );
                                } }>
                                  { singleSize.name }
                                </option>
                              </>
                            );
                          })
                          : "";
                      }) }

                    </select>
                  </div>
                </>
              ) : "" }

              <div className="form-group">
                <label htmlFor="price">Giá</label>
                <input
                  type="text"
                  name="price"
                  onChange={ handleChange }
                  value={ product.price }
                  className="form-control"
                />
              </div>

              <div className="form-group">
                <label htmlFor="body">Mô tả</label>
                <textarea
                  className="form-control"
                  name="description"
                  onChange={ handleChange }
                  value={ product.description }
                />
              </div>

              <div className="form-group">
                <label htmlFor="image">Tải hình ảnh</label>
                <div className="custom-file">
                  <input
                    type="file"
                    className="custom-file-input"
                    id="image"
                    // onChange={uploadFileHandler}
                    name="image"
                    onChange={ e => setImage(e.target.files[0]) }
                  // value={product.description}
                  />
                  <label htmlFor="image" className="custom-file-label">
                    Chọn hình ảnh
                  </label>
                </div>
                {/* <small className="form-text text-muted">Max Size 3mb</small> */ }
              </div>
            </div>
            <div className="modal-footer">
              <button
                className="btn btn-primary"
                type="submit"
                data-dismiss="modal"
                onClick={ handleAddproduct }>
                Thêm sản phẩm
              </button>
            </div>
            {/* </form> */ }
          </div>
        </div>
      </div>
    </>
  )
}

export default AddProductModal
