import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import UserContext from '../context/user/UserContext';

const UpdatePassword = () => {
  const navigate = useNavigate();
  const [fields, handleFieldChange] = useState({
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  // const [oldPassword, setOldPassword] = useState("");
  // const [newPassword, setNewPassword] = useState("");
  // const [confirmPassword, setConfirmPassword] = useState("");

  const uContext = useContext(UserContext);
  const { updatePassword } = uContext;

  const updatePasswordSubmit = (e) => {
    e.preventDefault();

    // const payload = { oldPassword, newPassword, confirmPassword };
    updatePassword(fields);
    // navigate('/profile')
  };

  return (
    <div>
      <section id="actions" className="py-4 mb-4 bg-light">
        <div className="container">
          <div className="row"></div>
        </div>
      </section>
      {/* LOGIN */ }
      <section id="login">
        <div className="container">
          <div className="row">
            <div className="col-md-6 mx-auto">
              <div className="card">
                <div className="card-header">
                  <h4>Đổi mật khẩu</h4>
                </div>
                <div className="card-body">
                  <form onSubmit={ updatePasswordSubmit }>
                    <div className="form-group">
                      <label htmlFor="password">Mật khẩu cũ</label>
                      <input
                        onChange={ handleFieldChange }
                        type="password"
                        className="form-control"
                        name="password"
                        value={ fields.oldPassword }
                      />

                    </div>

                    <div className="form-group">
                      <label htmlFor="password">Mật khẩu mới</label>
                      <input
                        onChange={ handleFieldChange }
                        type="password"
                        className="form-control"
                        name="newPassword"
                        value={ fields.newPassword }
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="password">Nhập mật khẩu mới</label>
                      <input
                        onChange={ handleFieldChange }
                        type="password"
                        className="form-control"
                        name="confirmPassword"
                        value={ fields.confirmPassword }
                      />
                    </div>


                    <input
                      type="submit"
                      defaultValue="ForgotPassword"
                      className="btn btn-primary btn-block"
                    />
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default UpdatePassword;
