import React, { useContext, useEffect, useState } from "react";
import { Form, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useCart } from "react-use-cart";
import Breadcrumb from "../components/Breadcrumb";
import CategoryContext from "../context/category/categoryContext";
import productContext from "../context/product/productContext";
import { getSortedProducts } from "../helpers/product";


const Shop = () => {
  // for product context
  const pContext = useContext(productContext);
  const { getProducts, products } = pContext;

  // for category context
  const cContext = useContext(CategoryContext);
  const { categories, getCategories } = cContext;

  const limit = 6;
  const [skip, setSkip] = useState(0);
  const [keyWord, setKeyWord] = useState("");
  const [category, setCategory] = useState("");
  const [totalResults, setTotalResults] = useState(0);
  const { addItem } = useCart();
  const [quantity, setQuantity] = useState(1);


  // const [sortType, setSortType] = useState('');
  // const [sortValue, setSortValue] = useState('');
  // const [filterSortType, setFilterSortType] = useState('');
  // const [filterSortValue, setFilterSortValue] = useState('');
  // // const [currentPage, setCurrentPage] = useState(1);
  // // const [currentData, setCurrentData] = useState([]);
  // const [sortedProducts, setSortedProducts] = useState([]);

  useEffect(() => {
    const populateProducts = async () => {
      setTotalResults(await getProducts(limit, skip, keyWord, category));
    };
    populateProducts();
    getCategories();

    const priceHighToLow = (value) => {
      const sortProducts = [...products];
      console.log("sortProducts", sortProducts);

      if (value === "default") {
        return sortProducts;
      }
      if (value === "priceHighToLow") {
        sortProducts.sort((a, b) => {
          console.log("priceHighToLow", sortProducts);
          return b.price - a.price;
        });
        return sortProducts
      }
      if (value === "priceLowToHigh") {
        sortProducts.sort((a, b) => {
          console.log("priceLowToHigh", sortProducts);
          return a.price - b.price;
        });
      }
      return sortProducts;
    }
    // setCurrentData(products)
    // eslint-disable-next-line
  }, [skip, limit, category, priceHighToLow]);




  // useEffect(() => {
  //   const sortArray = type => {
  //     const types = {
  //       albums: 'albums',
  //       members: 'members',
  //       formed: 'formed_in',
  //     };
  //     const sortProperty = types[type];
  //     const sorted = [...products].sort((a, b) => b[sortProperty] - a[sortProperty]);
  //     setData(sorted);
  //   };

  //   sortArray(sortType);
  // }, [sortType]);


  // useEffect(() => {
  //   let sortedProducts = getSortedProducts(products);
  //   const filterSortedProducts = getSortedProducts(sortedProducts,);
  //   sortedProducts = filterSortedProducts;
  //   // setSortedProducts(sortedProducts);
  //   // eslint-disable-next-line
  // }, [products]);

  // const getFilterSortParams = (sortType, sortValue) => {
  //   setFilterSortType(sortType);
  //   setFilterSortValue(sortValue);
  // }

  const handleChange = (e) => {
    setKeyWord(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    setSkip(0);
    setCategory("");
    const populateProducts = async () => {
      setTotalResults(await getProducts(limit, skip, keyWord, category));
    };
    populateProducts();
  };

  const handlePreviousClick = async () => {
    if (skip > 0) {
      setSkip(skip - limit);
    }
  };

  const handleNextClick = async () => {
    setSkip(skip + limit);
  };

  const formatter = new Intl.NumberFormat("it-IT", {
    style: "currency",
    currency: "VND",
  });

  return (
    <>
      <Breadcrumb pageName="Shop" />
      <div className="site-section">
        <div className="container">
          <div className="row mb-5">
            <div className="col-md-9 order-2">
              <div className="row">
                <div className="col-md-12 mb-5 d-flex justify-content-between">
                  <div className="float-md-left mb-4">

                    <h2 className="text-black h5">SHOP ALL</h2>
                    <select
                      onChange={ e => priceHighToLow(e.target.value) }
                    >
                      <option value="default">Mặc định</option>
                      <option value="priceHighToLow">Giá từ cao đến thấp</option>
                      <option value="priceLowToHigh">Giá từ thấp đến cao</option>
                    </select>
                  </div>
                  <div className="d-flex">
                    <Form className="d-flex" onSubmit={ handleSearchSubmit }>
                      <FormControl
                        type="search"
                        placeholder="Search products"
                        className="me-2"
                        aria-label="Search"
                        minLength={ 3 }
                        size="sm"
                        value={ keyWord }
                        onChange={ handleChange }
                      />
                      <button type="submit" className="btn btn-secondary mx-3">
                        Search
                      </button>
                    </Form>
                  </div>
                </div>
              </div>

              <div className="row mb-5">
                { products && products.map((product) => (
                  <div
                    className="col-sm-6 col-lg-4 mb-4"
                    data-aos="fade-up"
                    key={ product._id }
                  >
                    <div className="block-4 text-center border">
                      <figure className="block-4-image">
                        <Link to={ `/shopSingle/${product._id}` }>
                          <img
                            src={ product.image }
                            alt="placeholder"
                            className="img-fluid"
                          />
                        </Link>
                      </figure>
                      <div className="block-4-text p-4">
                        <h3>
                          <Link to={ `/shopSingle/${product._id}` }>
                            { product.name }
                          </Link>
                        </h3>
                        <p className="mb-0 text-secondary">
                          { product.category.title }
                        </p>
                        <p className="text-primary font-weight-bold">
                          { formatter.format(product.price) }
                        </p>
                        <p
                          className="buy-now btn btn-sm btn-primary"
                          onClick={ () => {
                            let item = {
                              ...product,
                              id: product._id,
                            };
                            addItem(item, quantity);
                          } }
                        >
                          {/* <Link
                        to="/Cart"
                        className="buy-now btn btn-sm btn-primary"
                        onClick={() => {
                          let item = {
                            ...product,
                            id: product._id,
                          };
                          addItem(item, quantity);
                        }}
                      >
                        Add To Cart
                      </Link> */}
                          add cart
                        </p>
                      </div>
                    </div>
                  </div>
                )) }
              </div>

              <div className="row" data-aos="fade-up">
                <div className="col-md-12 text-center">
                  <div className="d-flex justify-content-between align-items-center my-3">
                    <Button
                      variant="success"
                      size="sm"
                      onClick={ handlePreviousClick }
                      disabled={ skip < 1 }>
                      &larr; Trước
                    </Button>

                    <div className="text-center mx-2">
                      Trang -{ skip / limit + 1 },
                      <span className="text-muted">
                        { ' ' }
                        Hiển thị { products.length } trong số { totalResults }{ ' ' }
                        sản phẩm .
                      </span>
                    </div>

                    <Button
                      variant="success"
                      size="sm"
                      onClick={ handleNextClick }
                      disabled={ totalResults - skip <= limit }>
                      Tiếp tục &rarr;
                    </Button>
                  </div>
                </div>
              </div>
            </div>

