import React, { useContext, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Breadcrumb from "../components/Breadcrumb";
import productContext from "../context/product/productContext";
import { useCart } from "react-use-cart";

const ShopSingle = () => {
  const { addItem } = useCart();
  const [quantity, setQuantity] = useState(1);

  const { id } = useParams();
  const [product, setProduct] = useState({ category: {} });

  // for product context
  const pContext = useContext(productContext);
  const { getOneProduct } = pContext;

  useEffect(() => {
    const fetctProduct = async () => {
      const fetchedProduct = await getOneProduct(id);
      setProduct(fetchedProduct);
    };
    fetctProduct();
    // eslint-disable-next-line
  }, []);

  return (
    <>
      <Breadcrumb pageName={ product.name } />
      <div className="site-section">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <img src={ product.image } alt="img" className="img-fluid" />
            </div>
            <div className="col-md-6">
              <h2 className="text-black">{ product.name }</h2>
              <p>{ product.description }</p>
              <p>
                <small className="text-secondary">
                  { product.category.title }
                </small>
              </p>
              <p>
                <strong className="text-primary h4">${ product.price }</strong>
              </p>

              <div className="mb-5">
                <div className="input-group mb-3" style={ { maxWidth: "120px" } }>
                  <div className="input-group-prepend">
                    <button
                      disabled={ quantity < 2 }
                      className="btn btn-outline-primary js-btn-minus"
                      onClick={ () => setQuantity(quantity - 1) }
                      type="button"
                    >
                      &minus;
                    </button>
                  </div>
                  <input
                    type="text"
                    className="form-control text-center"
                    value={ quantity }
                    onChange={ (e) => setQuantity(e.target.value) }
                    disabled
                    placeholder=""
                    aria-label="Example text with button addon"
                    aria-describedby="button-addon1"
                  />
                  <div className="input-group-append">
                    <button
                      className="btn btn-outline-primary js-btn-plus"
                      onClick={ () => setQuantity(quantity + 1) }
                      type="button"
                      disabled={
                        quantity >= product.Stock
                      }
                    >
                      &#43;
                    </button>
                  </div>
                </div>
              </div>
              <p>

                { product.Stock >= 0 ? (<>
                  <Link
                    to="/Cart"
                    className="buy-now btn btn-sm btn-primary"
                    onClick={ () => {
                      let item = {
                        ...product,
                        id: product._id,
                      };
                      addItem(item, quantity);
                    } }
                  >
                    Thêm vào giỏ
                  </Link>
                </>) : (<>
                  <button
                    className="buy-now btn btn-sm btn-primary"
                  >
                    Hết hàng
                  </button>
                </>) }
              </p>
            </div>
          </div>
        </div>
      </div>
      <section>
        <div className="container">
          <div className="row">
            <div className="col-sm-5 col-md-6 col-12 pb-4">
              <h1>Comments</h1>
              <div className="comment mt-4 text-justify float-left">
                <h4>Jhon Doe</h4>
                <span>- 20 October, 2018</span>
                <br></br>
                <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Accusamus numquam assumenda hic aliquam vero sequi velit molestias doloremque molestiae dicta?</p>
              </div>
            </div>
            <div className="col-lg-4 col-md-5 col-sm-4 offset-md-1 offset-sm-1 col-12 mt-4">
              <form id="algin-form">
                <div className="form-group">
                  <h4>Leave a comment</h4>
                  <label for="message">Message</label>
                  <textarea name="msg" id="" msg cols="30" rows="5" className="form-control" ></textarea>
                </div>
                <div className="form-group">
                  <label for="name">Name</label>
                  <input disabled type="text" name="name" id="fullname" className="form-control" />
                </div>
                <div className="form-group">
                  <label for="email">Email</label>
                  <input disabled type="text" name="email" id="email" className="form-control" />
                </div>
                <div className="form-group">
                  <p className="text-secondary">If you have a <a href="#" className="alert-link">gravatar account</a> your address will be used to display your profile picture.</p>
                </div>
                <div className="form-inline">
                  <input type="checkbox" name="check" id="checkbx" className="mr-1" />
                  <label for="subscribe">Subscribe me to the newlettter</label>
                </div>
                <div className="form-group">
                  <button type="button" id="post" className="btn">Post Comment</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default ShopSingle;
