import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Navbar from "../AdminComponents/Navbar";
import OrderContext from "../context/orders/orderContext";
import "./or.css";
// import SweetPagination from "sweetpagination";
import { Form, FormControl } from "react-bootstrap";

const statusOrder = {
  Confirmed: "Đang xác nhận",
  Processing: "Đang giao hàng",
  Successfully: "Đã giao hàng",
  Canceled: "Đã hủy",

  // COMPLETED: "Đã Thanh toán online",
};
const total = (orders, status) =>
  orders.reduce((r, index) => {
    if (index.paymentResult.status === status) {
      if (index.orderItems.length) {
        const total = index.orderItems.reduce((count, item) => {
          return (count += item.price * item.quantity);
        }, 0);

        return (r += total);
      }
    }
    return r;
  }, 0);

const Orders = () => {
  // for order context
  const oContext = useContext(OrderContext);
  const { getAllOrders, orders, updateStatustAdmin } = oContext;
  const resulf = total(orders, "Successfully");
  const resulf2 = total(orders, "COMPLETED");
  const resulf3 = resulf + resulf2;

  const navigate = useNavigate();
  const [keyword, setKeyword] = useState("");

  const statusHtml = (orderStatus) => {
    const index = Object.keys(statusOrder).findIndex(
      (key) => key === orderStatus
    );

    return Object.keys(statusOrder)
      .map(function (key, i) {
        if (i >= index) {
          return `<option
          value="${key}"
          className={"text-gray-200 dark:text-slate-800"}
        >${statusOrder[key]}</option>`;
        }
        return "";
      })
      .join("");
  };
  const formatter = new Intl.NumberFormat("it-IT", {
    style: "currency",
    currency: "VND",
  });
  const limit = 6;
  const [keyWord, setKeyWord] = useState("");
  useEffect(() => {
    // getAllOrders();
    const orderseacrh = async () => {
      await getAllOrders(limit, keyWord);
    };
    orderseacrh();
  }, [limit]);
  const handleChange = (e) => {
    console.log(e);
    setKeyWord(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();

    const orderseacrh = async () => {
      await getAllOrders(limit, keyWord);
    };
    orderseacrh();
  };
  useEffect(() => {
    getAllOrders();
  }, []);

  const canceled = orders.map((i) => {
    let arr = [];
    if (i.paymentResult.status === "Canceled") {
      arr.push(i.paymentResult.status);
    }
    return arr;
  });
  const cance = canceled.filter((g) => g[0] === "Canceled");

  const searchHandler = (e) => {
    e.preventDefault();
    if (keyword.trim()) {
      navigate(`/products?${keyword}`);
    } else {
      navigate(`/products`)
    }
  }

  const handlerSearchChange = (e) => {
    setKeyword(e.target.value);
  }



  return orders ? (
    <>
      <Navbar />
      {/* HEADER */ }
      <header id="main-header" className="py-2 bg-info text-white">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <h1>
                <i className="fas fa-users" /> Đơn hàng
              </h1>
            </div>
          </div>
        </div>
      </header>

      {/* SEARCH */ }
      <section id="search" className="py-4 mb-4 bg-light">
        <div className="container">
          <div className="row">
            {/* <div>Doanh thu giao hàng: {formatter.format(resulf)}</div>
            <Link to={`/orderAdmin/cod`} className="btn btn-secondary">
              <i className="fas fa-angle-double-right" /> Chi tiết
            </Link>
            <div>
              Doanh thu đã thanh toán online: {formatter.format(resulf2)}
              <Link to={`/orderAdmin/online`} className="btn btn-secondary">
                <i className="fas fa-angle-double-right" /> Chi tiết
              </Link>
            </div>
            <div>Tổng doanh thu : {formatter.format(resulf3)}</div>
            <div>
              Đã hủy
              <Link to={`/orderAdmin/canceled`} className="btn btn-secondary">
                <i className="fas fa-angle-double-right" /> Chi tiết
              </Link>
            </div> */}
            {/* <div className="col-md-6 ml-auto">
              <div className="input-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search Users..."
                />
                <div className="input-group-append">
                  <button className="btn btn-warning">Tìm kiếm</button>
                </div>
              </div>
            </div> */}{ " " }
            <div className="col-md-6 ml-auto">
              <Form className="d-flex" onSubmit={ searchHandler }>
                <FormControl
                  type="search"
                  placeholder="Mã đơn hàng"
                  className="me-2"
                  aria-label="Search"
                  minLength={ 3 }
                  size="sm"
                  // value={ keyWord }
                  // onChange={ handleChange }
                  defaultValue={ keyword } onChange={ handlerSearchChange }
                />
                <button type="submit" className="btn btn-secondary mx-3">
                  Tìm kiếm
                </button>
              </Form>
            </div>
          </div>
        </div>
      </section>
      <div className="" style={ { display: 'flex', justifyContent: 'center', gap: '50px' } }>
        <div className="card text-center bg-success text-white mb-3">
          <div className="card-body">
            <h5>Doanh thu giao hàng</h5>
            <h4 className="display-4">
              <i className="fas fa-coins" />
            </h4>
            <h2>{ formatter.format(resulf) }</h2>
            <Link to={ `/orderAdmin/cod` } className="btn btn-outline-light btn-sm">
              Chi tiết
            </Link>
          </div>
        </div>

        <div className="card text-center bg-danger text-white mb-3">
          <div className="card-body">
            <h5>Doanh thu online</h5>
            <h4 className="display-4">
              <i className="fab fa-cc-paypal" />
            </h4>
            <h2>{ formatter.format(resulf2) }</h2>
            <Link to={ `/orderAdmin/online` } className="btn btn-outline-light btn-sm">
              Chi tiết
            </Link>
          </div>
        </div>

        <div className="card text-center bg-warning text-white mb-3">
          <div className="card-body">
            <h5>Đơn hàng đã hủy</h5>
            <h4 className="display-4">
              <i className="fas fa-window-close" />
            </h4>
            <h2>{ cance.length }</h2>
            <Link to={ `/orderAdmin/canceled` } className="btn btn-outline-light btn-sm">
              Chi tiết
            </Link>
          </div>
        </div>

        <div className="card text-center bg-primary text-white mb-3">
          <div className="card-body">
            <h5>Tổng doanh thu</h5>
            <h4 className="display-4">
              <i className="far fa-money-bill-alt" />
            </h4>
            <h2>{ formatter.format(resulf3) }</h2>
          </div>
        </div>
      </div>
      <section id="users">
        <div className="container">
          <div className="row">
            <div className="col">
              <div className="card">
                <div className="card-header">
                  <h4>Đơn đặt hàng mới nhất </h4>
                </div>
                <table className="table table-striped">
                  <thead className="thead-dark">
                    <tr>
                      <th>Mã</th>
                      <th>Tên người dùng</th>
                      <th>Ngày đặt hàng</th>
                      <th>Tiền </th>
                      <th>Trạng thái</th>
                      <th>Xem thêm</th>
                      <th />
                    </tr>
                  </thead>
                  <tbody>
                    { orders && orders.filter((value) => {
                      if (keyword === "") {
                        return value;
                      } else if (value.name.toLowerCase().includes(keyword.toLowerCase())) {
                        return value;
                      }
                    }).map((order, index) => (
                      <tr key={ order._id }>
                        {/* <td>{index + 1}</td> */ }
                        <td>{ order._id }</td>
                        <td>{ order.user?.name }</td>
                        <td>
                          { new Date(order.createdAt).toLocaleDateString() }
                        </td>
                        <td>{ formatter.format(order.totalPrice) }</td>
                        <td className="px-4 py-3">
                          <div className="flex-grow w-full online">
                            { order.paymentResult.status === "COMPLETED" ? (
                              "Đã thanh toán online"
                            ) : (
                              <select
                                onChange={ (e) =>
                                  updateStatustAdmin(e, order._id)
                                }
                                className="block w-full px-2 py-1 text-sm outline-none rounded-md form-select focus:shadow-none leading-5 h-12 bg-[#24262D] dark:bg-[#F4F5F7] border-[1px] border-gray-600 dark:border-gray-300 text-gray-200 dark:text-black"
                                name="orderStatus"
                                dangerouslySetInnerHTML={ {
                                  __html: statusHtml(
                                    order.paymentResult.status
                                  ),
                                } }
                              ></select>
                            ) }
                          </div>
                        </td>

                        <td>
                          <Link
                            to={ `/orderDetailsAdmin/${order._id}` }
                            className="btn btn-secondary bg-primary text-white"
                          >
                            <i className="fas fa-angle-double-right" /> Chi tiết
                          </Link>
                        </td>
                      </tr>
                    )) }
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>{ " " }
      </section>
    </>
  ) : "Đơn hàng trống"
};

export default Orders;
